	start_month = int_start_date[0]
	start_day = int_start_date[1]

	end_date = recorded_list[len(recorded_list)-1]
	end_date_components = end_date.split("/")
	int_end_date = list(map(int, end_date_components))
	end_year = int_end_date[2] + 2000
	end_month = int_end_date[0]
	end_day = int_end_date[1]

	date_1 = date(start_year, start_month, start_day)
	date_2 = date(end_year, end_month, end_day)
	delta = date_2 - date_1
	
	expected_list = []
	for i in range(delta.days + 1):
		unformatted_date = date_1 + timedelta(days=i)
		formatted_date = str(unformatted_date.strftime("%-m/%-d/%y"))
		expected_list.append(formatted_date)
	return expected_list


