def time_to_minutes(standard_datetime_format):
	twenty_four_hour_format = datetime.strftime(standard_datetime_format, "%H:%M")
	time_components = twenty_four_hour_format.split(":")
	time_hour = int(time_components[0])
	time_minute = int(time_components[1])
	time_in_minutes = (time_hour*60) + (time_minute)
	return time_in_minutes


def sleep_calculations():
	master_list = date_data()

	just_time = []
	for i in master_list:
		first_colon_place = i.find(":")
		just_time_string = i[first_colon_place+2:]
		just_time.append(just_time_string)

	# regular expressions
	period_re = re.compile(r'([A-Z])\w+')
	just_numbers_re = re.compile(r'\d+(:)?(\d+)?')
	
	# constants
	old_wake_up_time_in_minutes = 345
	max_minutes_in_day = 1440

	sleep_results = []
	for i in just_time:
		period_search = period_re.search(i)
		period_result = period_search.group()
		if "-" in i:
			components_of_two_times = i.split("-")
			time_1 = components_of_two_times[0].strip()
			time_2 = components_of_two_times[1].strip()	

			time_1_period_search = period_re.search(time_1)
			time_1_period_result = time_1_period_search.group()	

			time_2_period_search = period_re.search(time_2)
			time_2_period_result = time_2_period_search.group()	

			if ":" in time_1:
				standard_datetime_format = datetime.strptime(time_1, "%I:%M %p")
				time_1_in_minutes = time_to_minutes(standard_datetime_format)
			else:
				standard_datetime_format = datetime.strptime(time_1, "%I %p")
				time_1_in_minutes = time_to_minutes(standard_datetime_format)	

			if ":" in time_2:
				standard_datetime_format = datetime.strptime(time_2, "%I:%M %p")
				time_2_in_minutes = time_to_minutes(standard_datetime_format)
			else:
				standard_datetime_format = datetime.strptime(time_2, "%I %p")			
				time_2_in_minutes = time_to_minutes(standard_datetime_format)	

			if (time_1_period_result == "PM") and (time_2_period_result == "AM"):
				sleep_time = (max_minutes_in_day - time_1_in_minutes) + time_2_in_minutes
			else:
				sleep_time = time_2_in_minutes - time_1_in_minutes
		else:
			if ":" in i:
				standard_datetime_format = datetime.strptime(i, "%I:%M %p")
				one_time_in_minutes = time_to_minutes(standard_datetime_format)
				if period_result == "PM":
					sleep_time = (max_minutes_in_day - one_time_in_minutes) + old_wake_up_time_in_minutes
				else:
					sleep_time = old_wake_up_time_in_minutes - one_time_in_minutes
			else:
				standard_datetime_format = datetime.strptime(i, "%I %p")
				one_time_in_minutes = time_to_minutes(standard_datetime_format)
				if period_result == "PM":
					sleep_time = (max_minutes_in_day - one_time_in_minutes) + old_wake_up_time_in_minutes
				else:
					sleep_time = old_wake_up_time_in_minutes - one_time_in_minutes
		sleep_results.append(sleep_time)
	return sleep_results


